#Problem 8
#11 January 2002
#
#Find the greatest product of five consecutive digits in the 1000-digit number.
#
# Answer: 40824
#
class Problem8
  
  def initialize size=5
    @size = size
    @n = ""
  end
  
  def calculate
    gp = 0
    prod = 1
    0.upto(@n.size-@size) do |n|
      prod = 1
      str = @n.slice(n, @size)
      str.split(//).each do |s|
        prod *= s.to_i
      end
#      puts "... processando #{str} ... cujo produto Ã© #{prod} ..."
      gp = prod if prod > gp
    end
    gp
  end
  
end

